    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        this.IMC.Peso = Double.parseDouble(this.txtPeso.getText());
        this.IMC.Altura = Double.parseDouble(this.txtAltura.getText());
        this.IMC.calcular();
        String sMessage = this.IMC.mensagem(this.btnMasculino.isSelected());
        
        javax.swing.JOptionPane.showMessageDialog(rootPane, sMessage);
    }//GEN-LAST:event_btnCalculateActionPerformed

